mapper.delete:
DELETE FROM setmeal
WHERE id IN

    #{id}

mapper.select:
SELECT s.*, c.name AS categoryName FROM setmeal s LEFT OUTER JOIN category c ON s.category_id = c.id
AND s.name LIKE CONCAT('%', #{name}, '%')
AND s.category_id = #{categoryId}
AND s.status = #{status}
ORDER BY create_time DESC
